	/* When you need extra memory to execute the code please add this method to any of your controller method */
	public function AddMemory($memoryLimit = 512)
	{
		// return true;
		if ($memoryLimit > 512) {
			$memoryLimit = 512;
		}
		$memoryLimit = 6000;
		ini_set('memory_limit', $memoryLimit . 'M');
	}

	public function makeInsert($tablename, array $fields)
	{

		if ($tablename && $fields) {
			try {
				$fields = array_except($fields, ['_token', 'submitbtn']);

				return DB::table($tablename)->insert($fields);
			} catch (\Exception $e) {
				throw new \Exception($e->getMessage());
			}
		} else throw new \Exception('Error: ' . "Argument not passed");
	}

	public function makeInsertGetId($tablename, array $fields)
	{
		if ($tablename && $fields) {
			try {
				$fields = array_except($fields, ['_token', 'submitbtn']);
				return DB::table($tablename)->insertGetId($fields);
			} catch (\Exception $e) {
				throw new \Exception('Error: ' . $e->getMessage());
			}
		} else throw new \Exception("Argument not passed");
	}

	public function makeUpdate($tablename, array $update_fields, array $where)
	{ // can receive multiple cond in array
		if ($tablename && $update_fields) {
			try {
				$update_fields = array_except($update_fields, ['_token', 'submitbtn', 'user_id']);
				return  DB::table($tablename)->where($where)->update($update_fields);
			} catch (\Exception $e) {
				throw new \Exception('Error: ' . $e->getMessage());
			}
		} else throw new \Exception("Argument not passed");
	}

	public function makeDelete($tablename, array $fields)
	{ // can receive multiple cond in array
		if ($tablename && $fields && count($fields)) {
			try {
				return  DB::table($tablename)->where($fields)->delete();
			} catch (\Exception $e) {
				throw new \Exception('Error: ' . $e->getMessage());
			}
		} else throw new \Exception("Argument not passed");
	}

	public function getCountsByConditions($tablename, array $fields)
	{ //returns count
		if ($tablename && $fields && count($fields)) {
			try {
				return DB::table($tablename)->where($fields)->count();
			} catch (\Exception $e) {
				throw new \Exception('Error: ' . $e->getMessage());
			}
		} else throw new \Exception("Argument not passed");
	}

	public function getResultByConditions($table, $where = [], $select = [], $orderby = [], $limit = 0, $offset = 0)
	{ // Pass empty to skip condition param
		if ($table) {
			try {
				$response = DB::table($table);
				if (count($where))
					$response->where($where);
				if (count($select))
					$response->select($select);
				if (count($orderby)) {
					foreach ($orderby as $field => $sorttype) {
						$response->orderBy($field, $sorttype);
					}
				}

				if ($limit != 0) {
					$response->skip($offset);
					$response->take($limit);
				}

				return $response->get();
			} catch (\Exception $e) {
				throw new \Exception('Error: ' . $e->getMessage());
			}
		} else throw new \Exception("Argument not passed");
	}

	// type = source or destination
	public function  getAdditionalAccountDataByIntegrationId($userIntegrationId, $accType = 'source')
	{
		if ($accType = 'source') {
			return DB::table('platform_account_addtional_information as pi')
				->leftJoin('user_integrations as ui', 'ui.selected_sc_account_id', '=', 'pi.account_id')->first();
		} else {
			return DB::table('platform_account_addtional_information as pi')
				->leftJoin('user_integrations as ui', 'ui.selected_dc_account_id', '=', 'pi.account_id')->first();
		}
	}

	public function getFirstResultByConditions($table, $where = [], $select = [], $orderby = [], $whereIn = [])
	{
		if ($table) {
			try {
				$response = DB::table($table);
				if (count($where))
					$response->where($where);
				if (count($whereIn)) {
					foreach ($whereIn as $field => $data) {
						$response->whereIn($field, $data);
					}
				}
				if (count($select))
					$response->select($select);
				if (count($orderby)) {
					foreach ($orderby as $field => $sorttype) {
						$response->orderBy($field, $sorttype);
					}
				}
				return $response->first();
			} catch (\Exception $e) {
				throw new \Exception('Error: ' . $e->getMessage());
			}
		} else throw new \Exception("Argument not passed");
	}

	public function encryptString($key)
	{
		return base64_encode($key);
	}

	public function decryptString($key)
	{
		return base64_decode($key);
	}

	public function encryptStringOld($key)
	{
		return Crypt::encrypt($key);
	}

	public function decryptStringOld($key)
	{
		try {
			$res = Crypt::decrypt($key);
		} catch (DecryptException $e) {
			$res = false;
		}
		return $res;
	}

	public function ThrowErrorAndExit($msg)
	{
		echo '<h2>' . $msg . '</h2>';
	}

	/**
	 * Makes  API Request
	 * @param $method
	 * @param $url
	 * @param Array $post_data
	 * @param Array $header
	 * @return Array of response or error string (if request has error)
	 */
	public function makeRequest($method, $url, $post_data, $header = [], $type = "json")
	{
		$client = new \GuzzleHttp\Client();
		$options = [];

		if (!empty($post_data)) {
			if ($type == "http") {
				$options['form_params'] = $post_data;
			} else if ($type == "array") {
				$options['form_params'] = $post_data;
			} else if ($type == "json") {
				$options['json'] = $post_data;
			} else if ($type == "xml") {
				$options['body'] = $post_data;
			} else {
				$options['form_params'] = json_encode($post_data);
			}
		}
		if (!empty($header)) {
			$options['headers'] = $header;
		}

		try {
			$response = $client->request(
				$method,
				$url,
				$options,
				['connect_timeout' => 3.14],

			);

			return $response;
		} catch (\GuzzleHttp\Exception\RequestException $e) {

			if ($e->hasResponse()) {
				return $e->getResponse();
			}
		} catch (\GuzzleHttp\Exception\ClientException $e) {
			if ($e->hasResponse()) {
				return $e->getResponse();
			}
		} catch (\GuzzleHttp\Exception\BadResponseException  $e) {
			if ($e->hasResponse()) {
				return $e->getResponse();
			}
		} catch (\GuzzleHttp\Exception\ServerException   $e) {
			if ($e->hasResponse()) {
				return $e->getResponse();
			}
		} catch (\GuzzleHttp\Exception\TooManyRedirectsException  $e) {
			if ($e->hasResponse()) {
				return $e->getResponse();
			}
		}
	}

	public function makeCurlRequest($method, $url, $post_data, $header = [])
	{
		$request_arr = array(
			CURLOPT_URL => $url,
			CURLOPT_RETURNTRANSFER => true,
			CURLOPT_ENCODING => '',
			CURLOPT_MAXREDIRS => 10,
			CURLOPT_TIMEOUT => 0,
			CURLOPT_SSL_VERIFYHOST => 0,
			CURLOPT_SSL_VERIFYPEER => 0,
			CURLOPT_FOLLOWLOCATION => true,
			CURLOPT_HTTP_VERSION => CURL_HTTP_VERSION_1_1,
			CURLOPT_CUSTOMREQUEST => $method,
			CURLOPT_COOKIE => true,
		);

		if (in_array(strtolower($method), ['post', 'put', 'patch']) && $post_data) {
			$request_arr[CURLOPT_POSTFIELDS] = $post_data;
		}

		if (count($header)) {
			$request_arr[CURLOPT_HTTPHEADER] = $header;
		}
		// dd($request_arr);
		$curl = curl_init();
		curl_setopt_array($curl, $request_arr);

		$response = curl_exec($curl);
		if (!$response) {
			$response = curl_error($curl);
		}
		// dd($response);
		curl_close($curl);
		return $response;
	}

	public function makeUrlHttpsForProd($url)
	{
		if (env('APP_ENV') == 'prod')
			$url = str_replace("http://", "https://", $url);

		return $url;
	}

	public function encrypt_decrypt($string, $action = 'encrypt')
	{
		$encrypt_method = "AES-256-CBC";
		$secret_key = 'AA74CDCC2BBRT935136HH7B63C27'; // user define private key
		$secret_iv = '5fgf5HJ5g27'; // user define secret key | salt
		$key = hash('sha256', $secret_key);
		$iv = substr(hash('sha256', $secret_iv), 0, 16); // sha256 is hash_hmac_algo
		if ($action == 'encrypt') {
			$output = openssl_encrypt($string, $encrypt_method, $key, 0, $iv);
			$output = base64_encode($output);
		} else if ($action == 'decrypt') {
			$output = openssl_decrypt(base64_decode($string), $encrypt_method, $key, 0, $iv);
		}
		return $output;
	}

	public function getPlatformAccountByUserIntegration($user_integration_id, $platform_id, $select = [])
	{
		$response = false;
		try {
			$res = DB::table('user_integrations')->where('id', $user_integration_id)->pluck('selected_sc_account_id', 'selected_dc_account_id')->toArray();

			if (count($res)) {
				$keys = array_keys($res);
				$values = array_values($res);
				$merge = array_merge($keys, $values);
				$response = DB::table('platform_accounts')->whereIn('id', $merge)->where('platform_id', $platform_id);
				if (count($select)) {
					$response->select($select);
				}
				return $response->first();
			}
		} catch (\Exception $e) {
			throw new \Exception('Error: ' . $e->getMessage());
		}
	}

	public function mapOrderPaymentStatus($api_order_payment_status)
	{
		switch ($api_order_payment_status) {
			case 'PAID':
				$order_payment_status = 'paid';
				break;
			case 'PARTIALLY_PAID':
				$order_payment_status = 'partial_paid';
				break;
			case 'UNPAID':
				$order_payment_status = 'unpaid';
				break;
			case 'NOT_APPLICABLE':
				$order_payment_status = 'unpaid';
				break;
			case 'AUTHORIZED':
				$order_payment_status = 'unpaid';
				break;
			default:
				$order_payment_status = 'unpaid';
				break;
		}

		return $order_payment_status;
	}

	public function GetUserPlatformWorkFlowRules($user_integration_id)
	{
		$platform_workflow_rules = DB::table('user_integrations')
			->join('user_workflow_rule', 'user_workflow_rule.user_integration_id', '=', 'user_integrations.id')
			->join('platform_workflow_rule', 'user_workflow_rule.platform_workflow_rule_id', '=', 'platform_workflow_rule.id')
			->where('user_integrations.status', 1)
			->where('user_integrations.id', $user_integration_id)
			->select(
				'user_workflow_rule.id as user_workflow_rule_id',
				'user_workflow_rule.platform_workflow_rule_id',
				'platform_workflow_rule.source_event_id',
				'platform_workflow_rule.destination_event_id'
			)
			->orderBy('user_workflow_rule.id', 'ASC')->get()->toArray();
		return  $platform_workflow_rules;
	}

	//generate key for mapping data stored in cache to avoid db call
	public function generateIntegrationCacheKey($user_integration_id, $platform_workflow_rule_id, $mappingObjectName, $mapping_type)
	{
		return $user_integration_id . '_' . $platform_workflow_rule_id . '_' . $mappingObjectName . '_' . $mapping_type;
	}


