	public function checkHtmlTags(array $inputs)
	{

		foreach ($inputs as $input) {
			if ($input != strip_tags($input)) {
				return 1;
			}
		}
		return 0;
	}
	public function userAgents()
	{
		$userAgents = ([
			'Mozilla/5.0 (X11; Linux x86_64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/111.0.0.0 Safari/537.36',
			'Mozilla/5.0 (Macintosh; Intel Mac OS X 13_2_1) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/111.0.0.0 Safari/537.36',
			'Mozilla/5.0 (Windows NT 10.0; WOW64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/111.0.0.0 Safari/537.36',
			'Mozilla/5.0 (Windows NT 10.0) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/111.0.0.0 Safari/537.36',
		]);
		shuffle($userAgents);
		return $userAgents[0];
	}

	//$parentColumField = Auth::user()->id field(column) name
	//$authUserData = Auth::user(); auth user data
	//$tableName = table name where we search
	//$requestColumField = this is request id field(column) name
	public function checkParentAndPermission($parentColumField, $authUserData, $tableName, $requestColumField, $requestId)
	{

		$parent_user_id = $authUserData->id;
		$user_staff_id = isset($authUserData->staff_id) ? $authUserData->staff_id : null;
		$user_role =  $authUserData->role;

		$parentId =  DB::table($tableName)
			->where($requestColumField, $requestId)
			->where($parentColumField, $parent_user_id)
			->pluck($parentColumField)->first();

		$parentPermission = ($parentId == $parent_user_id) ? 1 : 0;

		if ($parentPermission && $user_role == 'user_staff') {
			$integrationModify = ModuleAccessController::getAccessRight($user_staff_id, $user_role, 'integrations', 'modify');
			$parentPermission = ($integrationModify == 1) ? 1 : 0;
		}

		return $parentPermission;
	}

