	//Log Token issue error msg in account Table for Email notification
	public function apiErrorLogForNotify($accountId,$message)
	{
		$find_account = DB::table('platform_accounts')->where('id',$accountId)->select('id','error_in_api_call')->first();
		if( $find_account && $find_account->error_in_api_call != 1 ) {
			DB::table('platform_accounts')->where('id',$find_account->id)->update(['error_in_api_call'=>1, 'error_msg' => $message]);
		}
		
	}

